tinymce.PluginManager.add('signup', function(editor, url) {
  var openDialog = function () {
    return editor.windowManager.open({
      title: 'Signup plugin',
      body: {
        type: 'panel',
        items: [
          {
            type: 'input',
            name: 'title',
            label: 'Title'
          }
        ]
      },
      buttons: [
        {
          type: 'cancel',
          text: 'Close'
        },
        {
          type: 'submit',
          text: 'Save',
          primary: true
        }
      ],
      onSubmit: function (api) {
        var data = api.getData();
        // Insert content when the window form is submitted
        editor.insertContent('<signup :a-href="1234">Something</signup>');
        api.close();
      }
    });
  };

  // Add a button that opens a window
  editor.ui.registry.addButton('signup', {
    text: 'My button',
    onAction: function () {
      // Open window
      openDialog();
    }
  });

  // Adds a menu item, which can then be included in any menu via the menu/menubar configuration
  editor.ui.registry.addMenuItem('signup', {
    text: 'Signup plugin',
    onAction: function() {
      // Open window
      openDialog();
    }
  });

  return {
    getMetadata: function () {
      return  {
        name: "Signup plugin",
        url: "http://www.agentquote.com/plugins"
      };
    }
  };
});